import React, { useEffect, useState } from "react";
import axios from "axios";

function App() {
  const [portfolio, setPortfolio] = useState([]);
  const [prices, setPrices] = useState({});
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchCoins = async () => {
      try {
        // 1. Get saved coins from your own backend
        const res = await axios.get("http://localhost:5000/api/coins");
        const coins = res.data;
        setPortfolio(coins);

        if (coins.length === 0) {
          setLoading(false);
          return;
        }

        // 2. Get live prices from CoinGecko
        const ids = coins.map(c => c.coinId).join(",");
        const priceRes = await axios.get(
          `https://api.coingecko.com/api/v3/simple/price?ids=${ids}&vs_currencies=usd`
        );
        setPrices(priceRes.data);
        setLoading(false);
      } catch (err) {
        console.error("Error:", err);
        setLoading(false);
      }
    };

    fetchCoins();
  }, []);

  if (loading) return <div>Loading...</div>;

  return (
    <div style={{ padding: "2rem", fontFamily: "Arial" }}>
      <h1>ðŸ“ˆ Crypto Portfolio Tracker</h1>
      {portfolio.length === 0 ? (
        <p>No coins added yet.</p>
      ) : (
        <table border="1" cellPadding="10">
          <thead>
            <tr>
              <th>Coin</th>
              <th>Amount</th>
              <th>Buy Price ($)</th>
              <th>Current Price ($)</th>
              <th>Profit / Loss (%)</th>
            </tr>
          </thead>
          <tbody>
            {portfolio.map((coin, idx) => {
              const current = prices[coin.coinId]?.usd || 0;
              const gain = (((current - coin.buyPrice) / coin.buyPrice) * 100).toFixed(2);
              return (
                <tr key={idx}>
                  <td>{coin.coinId}</td>
                  <td>{coin.amount}</td>
                  <td>{coin.buyPrice}</td>
                  <td>{current}</td>
                  <td style={{ color: gain >= 0 ? "green" : "red" }}>{gain}%</td>
                </tr>
              );
            })}
          </tbody>
        </table>
      )}
    </div>
  );
}

export default App;
